<style>
#highlight-wrapper {
  position: relative;
  max-width: 100%;
  height: max-content;
  margin: -600px auto 0;
  overflow: hidden;
}

#highlight-container {
  display: flex;
  padding: 3rem 2rem;
  overflow-x: scroll;
  scrollbar-width: none;
}

#highlight-container > * + * {
  margin-left: 4rem;
}

#highlight-container::-webkit-scrollbar {
  display: none;
}

#highlight-container a {
  flex-shrink: 0;
}

#highlight-left-paddle,
#highlight-right-paddle {
  position: absolute;
  width: 40px;
  height: 40px;
  z-index: 1;
  top: 50%;
  transform: translateY(-50%);
  color: white;
  background-color: rgba(0, 0, 0, 0.5);
  border-radius: 100%;
  cursor: pointer;
  transition: transform 0.3s ease-in-out;
  z-index: 5;
}

#highlight-left-paddle:focus,
#highlight-right-paddle:focus {
  border: 1px solid white;
  outline: none;
}

#highlight-left-paddle:hover,
#highlight-right-paddle:hover,
#highlight-left-paddle:focus,
#highlight-right-paddle:focus {
  transform: translateY(calc(-50% - 4px));
}

#highlight-left-paddle {
  left: 10px;
}

#highlight-right-paddle {
  right: 10px;
}
</style>

<script>
import StarterCard from '../../starter-card/src/StarterCard.vue';
import Card from '../../card/src/Card.vue';
import kits from './data';
import { setupScrollArrows, teardownScrollArrows } from './scroll-arrows.js';
import { setupDragHandling, teardownDragHandling } from './drag-handling.js';

export default {
  computed: {
    kits() {
      return kits;
    },
  },
  data() {
    return { Card, StarterCard };
  },
  mounted: function () {
    this.$nextTick(function () {
      setupScrollArrows(
        'highlight-left-paddle',
        'highlight-right-paddle',
        'highlight-container'
      );
      setupDragHandling('highlight-container');
    });
  },
  beforeUnmount: function () {
    teardownScrollArrows();
    teardownDragHandling();
  },
};
</script>
